import React ,{useState, useEffect}from "react"

const StarCard =({item, index,handleData})=>{

    const [active, setActive] = useState(0);

    const handleChangeActiveSuggestions = (e) => {
        console.log(e.keyCode, active);

        switch (e.keyCode) {
          case 40: { 
            setActive((prev) => prev + 1);
            document.querySelector("#container").style.color ="red";

            break;
          }

          case 38: {
            if (active === 1) {
              setActive(0);
            } else if (active <= 0) {
              setActive(item.length);
            } else {
              setActive((prev) => prev - 1);
            }

            break;
          }
          case 13: {
            console.log("hey");
            break;
          }
          default: {
            return;
          }
        }
      };


    return(

        <div onKeyUp={handleChangeActiveSuggestions}>

            <p onMouseOver={() => setActive(index + 1)} 
           >{item.name}</p>
        </div>
    )
}

export default StarCard
