import React from "react";
import {
  Button,
  Box,
  Typography,
  makeStyles,
  Dialog,
  DialogTitle,
  DialogActions,
  DialogContent,
  DialogContentText,
} from "@material-ui/core";

const GroceryItem = ({
  id,
  product_name,
  stock_available,
  quantity,
  price,
}) => {
  const useStyles = makeStyles({
    div: {
      border: "3px solid purple",
      width: "350px",
      margin: "50px",
      padding: "20px",
      color: "white",
    },
  });
  const random = () => {
    return `rgb(${Math.random() * 255},${Math.random() * 255},${
      Math.random() * 255
    })`;
  };
  let classes = useStyles();
  let background = random();
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleDelete = (id) => {
    handleClickOpen();
    axios.delete(`https://json-server-pavithra.herokuapp.com/grocery/${id}`);
  };
  return (
    <div
      style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}
    >
      <Box color="text.primary" className={classes.div} style={{ background }}>
        <Typography variant="h4" color="inherit">
          Product Name : {product_name}{" "}
        </Typography>

        <Typography> Quantity : {quantity}</Typography>
        <Typography> Price : {price}</Typography>
        <Button variant="contained" color="primary">
          {stock_available ? "Stock Available" : "Stock Not Aavilable"}
        </Button>
        <button onClick={() => handleDelete(id)}> DELETE</button>
      </Box>

      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Press Yes to Add Items ?"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Are you Sure to Add the Grocery
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Yes
          </Button>
          <Button onClick={handleClose} color="primary" autoFocus>
            No
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

const MemoisedGrocery = React.memo(
  GroceryItem,
  (prev, next) => prev.id === next.id
);

export { GroceryItem, MemoisedGrocery };
