import styled  from "styled-components";
import React ,{useState} from "react"

const SearchBarWrapper = styled.div`
border:1px solid black;
border-radius: 20px;
padding:10px;
display:flex;
position:realtive;
width:500px;
margin:auto;
`;

const IconImage = styled.img`
height:20px;
padding:20px;
`;

const input = styled.div`
    flex:1;
    /* border:1px solid black; */
    /* outline:none; */
    ;
`;

const RightSide = styled.div`
    display:flex;
    flex:0 0 auto;
    padding-right: 10px;
    margin-left: 10px;
    margin-top: 15px;

`;

const Spinner = styled.div`

  border: 2px solid #f3f3f3; /* Light grey */
  border-top: 2px solid #3498db; /* Blue */
  border-radius: 50%;
  width: 20px;
  padding:2px;
  margin-left: 10px;
  height: 20px;
  animation: spin 2s linear infinite;

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}
`;
const SearchBar = ({loading, setLoading, value, onChange, suggestions })=>{
const [ q, setQ] = useState("")
// const [loading,setLoading] = useState(false)

const handleinputChange=(e)=>{

        setQ(e.target.value);
        setLoading(true)
}

const handleClear =()=>{
    setQ("");
    setLoading(false)
}
    return(
<>
        <h1> Search Bar</h1> 
               <SearchBarWrapper>

            <IconImage src="https://image.flaticon.com/icons/png/512/622/622669.png"
            alt ="search-icon"
            />

            <input type="text" value ={q} onChange={handleinputChange}/>

            <RightSide>

                {q && <div onClick ={handleClear}>X</div>}
                {loading && <Spinner></Spinner>}

            </RightSide>
        </SearchBarWrapper>
        </>
    )
}

export default SearchBar;
