import { getDefaultNormalizer } from "@testing-library/dom"
import React from "react"
import {useState, useHistory} from "react"
import { useDebouncer, useFetch } from "../CustomHooks/useHooks"
import "./StarFetch.css"
import StarCard from "./StarCard"
const StarFetch =()=>{

    const [query, setQuery] = useState("")
    const [active, setActive] = useState(0);
    const debouncedQuery = useDebouncer(query, 500)
    // const {isLoading, isError, data} = useFetch(`https://api.github.com/search/users?q=${debouncedQuery || "masai"}`)
    const {isLoading, isError, data} = useFetch(`https://swapi.dev/api/people/?search=${debouncedQuery || "luke"}`)
    const handleChangeActiveSuggestions = (e) => {
        console.log(e.keyCode, active);
        // console.log(e.target.scrollTop);
        // e.current.scrollTop += 10;
        switch (e.keyCode) {
          case 40: {
            setActive((prev) => prev + 1);
            document.querySelector("#container").style.color ="red";

            break;
          }

          case 38: {
            if (active === 1) {
              setActive(0);
            } else if (active <= 0) {
              setActive(data.length);
            } else {
              setActive((prev) => prev - 1);
            }

            break;
          }
          case 13: {
            console.log("hey");
            break;
          }
          default: {
            return;
          }
        }
      };

        return (
            <>

            <h1>Star wars Search</h1>
            <input type="text" name="" id="" value= {query} onChange ={(e)=>setQuery(e.target.value)}/>
            <div onKeyUp={handleChangeActiveSuggestions} id="container">
            {
                isLoading ? <div> Loading...</div>
                : isError ? <div> Error... </div>
                :
                // data.results && data.results?.map((item, index)=><p key={item.url} onMouseOver={() => setActive(index + 1)}> {item.name}  hey
                // </p>

                // )
                data.results && data.results?.map((item, index)=><StarCard key={item.url} onMouseOver={() => setActive(index + 1)}/>

                )
            }
            </div>
            </>
        )
}

export default StarFetch;
