import React from "react";
import { useFetch, postGrocery } from "../Hooks/useFetch";
import { GroceryItem } from "./GroceryItem";
import axios from "axios";
import {
  Dialog,
  Button,
  DialogTitle,
  DialogActions,
  DialogContent,
  DialogContentText,
  makeStyles,
  TextField
} from "@material-ui/core";

const GroceryList = () => {
  const initState = {
    product_name: "",
    stock_available: true,
    quantity: "",
    price: "",
  };

  const [postData, setPostData] = React.useState(initState);
  const { isLoading, isError, data } = useFetch(
    `https://json-server-pavithra.herokuapp.com/grocery`
  );

  const { product_name, quantity, price } = postData;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPostData({ ...postData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    let payload = {
      product_name: postData.product_name,
      quantity: postData.quantity,
      price: postData.price,
      id: Date.now(),
      stock_available: true,
    };
    console.log(payload);
    postGrocery("https://json-server-pavithra.herokuapp.com/grocery", payload);
    axios
      .post("https://json-server-pavithra.herokuapp.com/grocery", payload)
      .then((res) => {
        console.log(res.data);
        handleClickOpen();
      });
  };
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  const useStyles = makeStyles({
    div: {
      border: "3px solid purple",
      width: "350px",
      margin: "50px",
      padding: "20px",
      color: "white",
    },
    button:{
        margin:"10px"
    }
  });
  let classes = useStyles();

  return (
    <>
      <h4> GROCERY LIST </h4>

      <form onSubmit={handleSubmit}>
        <TextField
          type="text"
          name="product_name"
          id="product_name"
          value={product_name}
          onChange={handleChange}
          variant="outlined"
          label="Enter Product Name"
        />
      {" "}
        <TextField
          type="text"
          name="quantity"
          id="quantity"
          value={quantity}
          onChange={handleChange}
          variant="outlined"
          label="Enter Product Quantity"
        />
        <br /> <br />{" "}
        <TextField
          type="text"
          name="price"
          id="price"
          value={price}
          onChange={handleChange}
          variant="outlined"
          label="Enter Product Price"
        />
        <Button> A D D Grocery </Button>
      </form>

      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Press Yes to Add Items ?"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Are you Sure to Add the Grocery
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Yes
          </Button>
          <Button onClick={handleClose} color="primary" autoFocus>
            No
          </Button>
        </DialogActions>
      </Dialog>


      {isLoading ? (
        <div>Loading... </div>
      ) : isError ? (
        <div> Error....</div>
      ) : (
        data?.map((item) => <GroceryItem key={item.id} {...item} />)
      )}
    </>
  );
};

export { GroceryList };
