import React from "react"
import { useFetch } from "../Hooks/useFetch"
import { GroceryItem } from "./GroceryItem"

const GroceryList =()=>{


    const initState ={
        product_name:"",
        stock_available: true,
        quantity:"",
        price:""
    }


    const [postData, setPostData] = React.useState(initState)
    const {isLoading, isError, data} = useFetch(`https://json-server-pavithra.herokuapp.com/grocery`)

        const { product_name, quantity , price} = postData
    const handleChange =(e)=>{
        const {name,value} = e.target
        setPostData({...postData, [name]:value})
    }

    const handleSubmit =(e)=>{
        e.preventDefault()

        let payload = {
            product_name :postData.product_name,
            quantity  : postData.quantity,
            price : postData.price,
            id: Date.now(),
            stock_available:true
        }
        console.log(payload)
    }
    return(
        <>

            <h4> GROCERY LIST </h4>

            <form onSubmit={handleSubmit}>
                    <input type="text" name="product_name" id="product_name" value ={product_name} onChange={handleChange}/>
                  <br/> <br/>  <input type="text" name="quantity" id="quantity" value ={quantity} onChange={handleChange}/>
                  <br/> <br/> <input type="text" name="price" id="price" value ={price} onChange={handleChange}/>

                    <button>POST</button>
                </form>

            {
                isLoading ? <div>Loading... </div> : isError ? <div> Error....</div>:
                data?.map((item)=><GroceryItem key={item.id} {...item}/>)
            }


        </>
    )
}

export {GroceryList}
