import React, { createContext } from "react";

const theme = {
  dark: {
    color: "#FFFFF",
    background: "#121212",
  },
  light: {
    color: "#121212",
    background: "#FFFF",
  },
};

export const AppContext = createContext();

function AppProvider(props) {
  const [appTheme, setAppTheme] = React.useState(theme.light);

  const setDark = () => setAppTheme(theme.dark);
  const setLight = () => setAppTheme(theme.light);

  const value = {
    appTheme,
    setDark,
    setLight,
  };

  return <AppProvider value={value}> {props.children}</AppProvider>;
}

export default AppProvider;
